import{_ as e,c as t,o as s,d as o}from"./app.c63e1970.js";var i="/letras/docs/assets/pause_sliders.5a58bf81.jpg",n="/letras/docs/assets/title_screen.435aebb0.jpg",a="/letras/docs/assets/settings_menu.2a36acd9.jpg",r="/letras/docs/assets/music_player.01876fbf.jpg",l="/letras/docs/assets/song_selection_screen.48ad17ad.jpg",c="/letras/docs/assets/typing_screen.f82f332f.jpg",h="/letras/docs/assets/pause_screen.016be4dc.jpg",d="/letras/docs/assets/score_screen.e92393f0.jpg",g="/letras/docs/assets/song_files.601ec934.jpg",p="/letras/docs/assets/song_to_edit_screen.18ef08b3.jpg",u="/letras/docs/assets/song_information_not_complete.5d142f0d.jpg",m="/letras/docs/assets/song_information_complete.d5dc0d18.jpg",y="/letras/docs/assets/lyric_insertion.f6395c40.jpg",f="/letras/docs/assets/lyrics_inserted.193e9fcd.jpg",b="/letras/docs/assets/subtitle_insertion.fcdefefe.jpg",v="/letras/docs/assets/song_synchronized.954a0e27.jpg",k="/letras/docs/assets/subtitles_synchronized.b454dbb0.jpg",w="/letras/docs/assets/lyric_position.e375c868.jpg",T="/letras/docs/assets/another_lyricmap.17b9b9dc.jpg";const W='{"title":"Letras Documentation","description":"","frontmatter":{},"headers":[{"level":2,"title":"General Information","slug":"general-information"},{"level":3,"title":"Game Navigation","slug":"game-navigation"},{"level":3,"title":"How to play","slug":"how-to-play"},{"level":3,"title":"Opacity and speed","slug":"opacity-and-speed"},{"level":3,"title":"Volume","slug":"volume"},{"level":3,"title":"Song location","slug":"song-location"},{"level":2,"title":"Screens","slug":"screens"},{"level":3,"title":"Title","slug":"title"},{"level":3,"title":"Settings","slug":"settings"},{"level":3,"title":"Music player","slug":"music-player"},{"level":3,"title":"Song selection","slug":"song-selection"},{"level":3,"title":"Typing","slug":"typing"},{"level":3,"title":"Pause","slug":"pause"},{"level":3,"title":"Score","slug":"score"},{"level":2,"title":"Song Editing","slug":"song-editing"},{"level":3,"title":"Creating a song folder","slug":"creating-a-song-folder"},{"level":3,"title":"Adding a background","slug":"adding-a-background"},{"level":3,"title":"New song","slug":"new-song"},{"level":3,"title":"Properties","slug":"properties"},{"level":3,"title":"Lyric insertion","slug":"lyric-insertion"},{"level":3,"title":"Subtitles","slug":"subtitles"},{"level":3,"title":"Timing","slug":"timing"},{"level":3,"title":"Position","slug":"position"},{"level":3,"title":"Difficulties","slug":"difficulties"},{"level":3,"title":"Fast lyrics","slug":"fast-lyrics"}],"relativePath":"index.md","lastUpdated":1641670371972}',_={},x=o('<h1 id="letras-documentation" tabindex="-1">Letras Documentation <a class="header-anchor" href="#letras-documentation" aria-hidden="true">#</a></h1><p>In this page you will find everything about the game.</p><h2 id="general-information" tabindex="-1">General Information <a class="header-anchor" href="#general-information" aria-hidden="true">#</a></h2><h3 id="game-navigation" tabindex="-1">Game Navigation <a class="header-anchor" href="#game-navigation" aria-hidden="true">#</a></h3><p>Navigate the menus using the keyboard or mouse.<br> Pressing <strong>ESC</strong> will bring you back to the previous screen or menu.</p><h3 id="how-to-play" tabindex="-1">How to play <a class="header-anchor" href="#how-to-play" aria-hidden="true">#</a></h3><p>The game is played using just the keyboard.<br> You can only type using the 26 letters from the US-Keyboard (A to Z).<br> Press the <strong>ENTER</strong> key to skip a line if you think you won&#39;t be able to complete it. When a line is skipped, you will lose your combo, but you will have more time for the next line.</p><p>There is no use for the <strong>BACKSPACE</strong>.<br> You can press the <strong>ESC</strong> key whenever you want to pause the game. This won&#39;t affect your score.</p><p>If a song has an intro with no lyrics, you can skip it by clicking on the <em>SKIP</em> word, pressing the <strong>SPACEBAR</strong> or the <strong>ENTER</strong> key.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>In this game you&#39;re not required to type spaces, so don&#39;t do it and you will be able to type faster.</p></div><h3 id="opacity-and-speed" tabindex="-1">Opacity and speed <a class="header-anchor" href="#opacity-and-speed" aria-hidden="true">#</a></h3><p>Change the background opacity of the songs or the vertical line speed on the pause menu. You can access it by pressing <strong>ESC</strong> while playing a song.</p><p><img src="'+i+'" alt="sliders"></p><p>The sliders can only be ajusted by clicking and dragging.</p><h3 id="volume" tabindex="-1">Volume <a class="header-anchor" href="#volume" aria-hidden="true">#</a></h3><p>Change the game volume anywhere by whether using the <strong>SCROLL WHEEL</strong> or pressing <strong>CTRL + UP/DOWN</strong> arrows.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The scroll wheel won&#39;t controle the volume if you&#39;re above an scrolleable list or menu.</p></div><h3 id="song-location" tabindex="-1">Song location <a class="header-anchor" href="#song-location" aria-hidden="true">#</a></h3><p>All the songs you download must be placed inside<code>letras/songs/</code>folder. Each song is a folder containing various files. Don&#39;t put zip/rar files inside.</p><h2 id="screens" tabindex="-1">Screens <a class="header-anchor" href="#screens" aria-hidden="true">#</a></h2><h3 id="title" tabindex="-1">Title <a class="header-anchor" href="#title" aria-hidden="true">#</a></h3><p><img src="'+n+'" alt="Title screen"></p><ul><li><strong>START</strong>: goes to the song selection screen</li><li><strong>EDIT</strong>: goes to the edit song selection screen</li><li><strong>SETTINGS</strong>: displays the settings menu on the left</li></ul><p>Clicking the song name at the top will bring down the music player.<br> Pressing <strong>ESC</strong> will bring down the exit prompt menu.</p><h3 id="settings" tabindex="-1">Settings <a class="header-anchor" href="#settings" aria-hidden="true">#</a></h3><p><img src="'+a+'" alt="Settings menu"></p><h4 id="window-size" tabindex="-1">Window size <a class="header-anchor" href="#window-size" aria-hidden="true">#</a></h4><p>Allows you to change the game&#39;s resolution or to make it fullscreen.</p><p>Available options:</p><ul><li>1280x720</li><li>1600x900</li><li>Fullscreen</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The window can be resized by dragging it by its corner with the mouse.</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Enter Fullscreen mode anywhere by pressing <strong>ALT+ENTER</strong>.</p></div><h4 id="language" tabindex="-1">Language <a class="header-anchor" href="#language" aria-hidden="true">#</a></h4><p>You can choose between <strong>English</strong> or <strong>Spanish</strong>.</p><h4 id="reload-songs" tabindex="-1">Reload songs <a class="header-anchor" href="#reload-songs" aria-hidden="true">#</a></h4><p>Click this if you modified a song file and it&#39;s not showing properly.</p><h4 id="webpage" tabindex="-1">Webpage <a class="header-anchor" href="#webpage" aria-hidden="true">#</a></h4><p>You can click the link to the game&#39;s webpage.</p><h3 id="music-player" tabindex="-1">Music player <a class="header-anchor" href="#music-player" aria-hidden="true">#</a></h3><p><img src="'+r+'" alt="Music player"></p><p>Classic media player buttons.<br> Type to search for songs and press <strong>ENTER</strong> to play it.<br> Navigate through the list using the mouse scroll.<br> To go back, just press <strong>ESC</strong> or click on the arrow on the right side.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Songs will play in order by default. You need to press the shuffle button everytime you launch the game if you want it to be random.</p></div><h3 id="song-selection" tabindex="-1">Song selection <a class="header-anchor" href="#song-selection" aria-hidden="true">#</a></h3><p><img src="'+l+'" alt="Song selection"></p><p>On the left is the song information and the song list on the right.</p><p>On the left part you can find, name, song length, difficulty (version), and a CPM graph. <strong>CPM</strong> stands for &quot;Characters per minute&quot;.</p><p>You can change difficulty or version by pressing <strong>LEFT/RIGHT</strong> arrows or clicking the white arrows on the side of the song (these will only appear if the song has more than one version).</p><p>The CPM graph will give you more or less information about the actual difficulty through the song. The version name and color is decided by the map author.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>CPM is calculated based on characters you need to type. Spaces are omitted in the calculation.</p></div><h3 id="typing" tabindex="-1">Typing <a class="header-anchor" href="#typing" aria-hidden="true">#</a></h3><p><img src="'+c+'" alt="typing screen"></p><p>The space in between the 2 yellow lines is the space where you can type. You don&#39;t need to select anything with the mouse, just type.</p><p>To skip a line press <strong>ENTER</strong> when the line is on the typing area.<br> To pause the game press <strong>ESC</strong></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Skipping lines is really useful if you get stuck or mess up badly.</p></div><h3 id="pause" tabindex="-1">Pause <a class="header-anchor" href="#pause" aria-hidden="true">#</a></h3><p><img src="'+h+'" alt="pause screen"></p><ul><li><strong>Retry</strong>: Restarts the song.</li><li><strong>Exit</strong>: Goes back to the song selection screen.</li></ul><p>If lines are too close together, you can increase the line speed to make them faster and more apart from each other.</p><p>If you have trouble reading the lyrics, increasing the background opacity may help.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The Retry button is focused by default, so you can retry a song very quickly by pressing <strong>ESC</strong> and then <strong>ENTER</strong>.</p></div><h3 id="score" tabindex="-1">Score <a class="header-anchor" href="#score" aria-hidden="true">#</a></h3><p><img src="'+d+'" alt="Score screen"></p><ul><li><strong>Mistakes</strong>: Typos or characters you got wrong</li><li><strong>Missed lines</strong>: Lines that you didn&#39;t finish.</li><li><strong>Omitted lines</strong>: The amount of times you pressed <strong>ENTER</strong> to skip a line.</li></ul><p>The colored circle on the right shows the amount of correct typed characters in relation to the total amount of the song.</p><h2 id="song-editing" tabindex="-1">Song Editing <a class="header-anchor" href="#song-editing" aria-hidden="true">#</a></h2><h3 id="creating-a-song-folder" tabindex="-1">Creating a song folder <a class="header-anchor" href="#creating-a-song-folder" aria-hidden="true">#</a></h3><p><img src="'+g+'" alt="song files"></p><p>To create a new song, you start by creating a new folder inside<code>letras/songs</code>. This folder can be named whatever you want, on the image it&#39;s named &quot;<em>sample song</em>&quot;.</p><p>Inside your song folder you must put the song in <strong>MP3</strong> format.</p><h3 id="adding-a-background" tabindex="-1">Adding a background <a class="header-anchor" href="#adding-a-background" aria-hidden="true">#</a></h3><p>If you want to add a <strong>background image</strong> you can either use a <strong>PNG</strong> or <strong>JPG</strong> file. This file is recommended to be 1280x720 resolution and it must be called <strong>background</strong>.</p><p>If you want to add a <strong>background video</strong> it must be a <strong>WEBM</strong> file with no sound and it must be named <strong>video</strong>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Background image and video are completely optional. I highly recommend at least using a background image.</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>I recommend using <a href="https://handbrake.fr" target="_blank" rel="noopener noreferrer"><strong>Handbrake</strong></a> for converting videos to the WEBM format.</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Remember, naming the files &quot;background&quot; and &quot;video&quot; is important. They won&#39;t be recognized if you don&#39;t.</p></div><h3 id="new-song" tabindex="-1">New song <a class="header-anchor" href="#new-song" aria-hidden="true">#</a></h3><p>Let&#39;s say we want to create a lyricmap for Flashback of \u79CB\u8D64\u97F3.</p><p>After <a href="#creating-a-song-folder">creating the folder</a> and putting the song, background image and video inside. We should click/press <strong>EDIT</strong> from the <a href="#title">Title screen</a>.</p><p><img src="'+p+'" alt="song to edit"></p><p>This is what we should see. We select it and we will go to the next screen.</p><h3 id="properties" tabindex="-1">Properties <a class="header-anchor" href="#properties" aria-hidden="true">#</a></h3><p><img src="'+u+'" alt="song to edit"></p><p>This is the first screen you will see once you enter edit mode.</p><ul><li><strong>Song name</strong>: The actual name of the song.</li><li><strong>Artist</strong>: The original artist of the song.</li><li><strong>Preview time</strong>: Which part of the song will play on the selection screen.</li><li><strong>Map author</strong>: Name of whoever created the lyricmap.</li><li><strong>Version name</strong>: This could be anything, but you should use words like: easy, hard, insane, etc.</li><li><strong>Version color</strong>: Choose the most suitable color for your version name.</li></ul><p>On the bottom left corner, you can see the name of the file you&#39;re editing. In this case it&#39;s &quot;<em>lyricmap.json</em>&quot;. This is useful when you&#39;re editing <a href="#difficulties">different difficulties</a>.</p><p>When you&#39;re finish click <strong>Save</strong> on the bottom right corner.</p><p>Example of how it should look like:</p><p><img src="'+m+'" alt="song information"></p><h3 id="lyric-insertion" tabindex="-1">Lyric insertion <a class="header-anchor" href="#lyric-insertion" aria-hidden="true">#</a></h3><p>Click on the <strong>Input</strong> and click the <strong>Insert lyrics</strong> button. You should paste the lyrics on the pop-up window.</p><p><img src="'+y+'" alt="lyric insertion"></p><p>You can edit them if needed. Click the <strong>Insert</strong> button to insert the lyrics.<br> This is how it should look like:</p><p><img src="'+f+'" alt="inserted lyrics"></p><p>If you see a line turning red, it means it contains non-typeable characters such as<code>? ! @ , . &#39; - /</code>etc. The only characters you can put in these boxes are letters from A to Z.</p><h3 id="subtitles" tabindex="-1">Subtitles <a class="header-anchor" href="#subtitles" aria-hidden="true">#</a></h3><p><img src="'+b+'" alt="subtitle insertion"></p><p>You should insert the subtitles before timing the lyrics. Since they are most likely to be in the same order and have the same amount of lines. When you create or remove lines, they will be removed in both <strong>Input</strong> and <strong>Subtitle</strong> tab.</p><p>Insert them using the <strong>Insert subtitles</strong> button just like before.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>You can put any kind of character you want on these lines. But stick to punctuation and supported languages such as: English, Spanish, Japanese, Chinese and Korean. If you paste something and is not showing up, it means it&#39;s not supported.</p></div><h3 id="timing" tabindex="-1">Timing <a class="header-anchor" href="#timing" aria-hidden="true">#</a></h3><p>Press the play button on the top to listen to the song. The timestamp on the right will start running. You should use the buttons <strong>Set start</strong> and <strong>Set end</strong> on each line to set up the timings. You can also use <strong>F9</strong> and <strong>F10</strong> keys respectively.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Whenever you press <strong>Set start</strong>, you will advance to the line below. This means that pressing <strong>Set end</strong> will affect the line above your current line.</p></div><p>The <strong>-/+</strong> buttons on the right remove the current line or inserts one new line below.</p><div class="danger custom-block"><p class="custom-block-title">WARNING</p><p>There is no <strong>Undo</strong> button so be careful when removing lines.</p></div><p>After you synchronize the lyrics it should look something like this:</p><p><img src="'+v+'" alt="lyric insertion"></p><p>Try to break the lines apart according to how the song goes.</p><p>Keep an eye on the CPM on the right of each line. If It&#39;s too high you can try splitting the lines differently or using the <a href="#fast-lyrics">special characters</a>.</p><p>Remember to make sure the subtitles are aligned with the lyrics.</p><p><img src="'+k+'" alt="synchronized subtitles"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Duplicating the subtitles instead of splitting them just like the lyrics give a better result.</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>If you don&#39;t <strong>SAVE</strong> before leaving the screen, your progress will be lost. Save before clicking on the <strong>Position</strong> button.</p></div><h3 id="position" tabindex="-1">Position <a class="header-anchor" href="#position" aria-hidden="true">#</a></h3><p><img src="'+w+'" alt="position edition"></p><p>This is the easier part. Just drag the lines horizontally with the mouse. The whole idea is to make them readable.</p><p>You can adjust their position pixel by pixel by using the <strong>LEFT/RIGHT</strong> arrows while cliking a line.</p><p>Click the <strong>Hotkeys</strong> button to display all the shortcuts you can use.</p><p>Click the gear button on the top right to change <strong>background opacity</strong> or <strong>line speed</strong>.</p><h4 id="play-testing" tabindex="-1">Play testing <a class="header-anchor" href="#play-testing" aria-hidden="true">#</a></h4><p>On the same position screen, you can play test the song just by typing as if you were playing.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>You can pause the song with <strong>SPACEBAR</strong> and go forwards/backwards 1 second using the <strong>UP/DOWN</strong> keys respectively.</p></div><h3 id="difficulties" tabindex="-1">Difficulties <a class="header-anchor" href="#difficulties" aria-hidden="true">#</a></h3><p>To create a new difficulty for the same song, go to the song folder and duplicate the <code>lyricmap.json</code> file. Name it differently like <code>lyricmap[insane].json</code>. Go to the edit song selection, search for your song and select the other version.</p><p>The versions may look the same, since they are duplicates. Make sure to check the filename on the bottom of the <strong>Properties tab</strong> so you know which file are you editing.</p><p><img src="'+T+'" alt="different lyricmap"></p><p>Remember to change the name version and color to make them distinct.</p><h3 id="fast-lyrics" tabindex="-1">Fast lyrics <a class="header-anchor" href="#fast-lyrics" aria-hidden="true">#</a></h3><p>If you think the song is too fast, or the lines you created have too much CPM. You can try using the special characters<code>[</code>and<code>]</code>.</p><p>After entering the special characters, you will only need to type what&#39;s inside the brackets.</p><p>Examples of how it works:</p><table><thead><tr><th>Input line</th><th style="text-align:center;">What you need to type</th></tr></thead><tbody><tr><td>This is a line of lyrics</td><td style="text-align:center;">This is a line of lyrics</td></tr><tr><td>[This is a line of lyrics]</td><td style="text-align:center;">This is a line of lyrics</td></tr><tr><td>[T]his [is a line of lyrics]</td><td style="text-align:center;">T is a line of lyrics</td></tr><tr><td>[T]his [i]s [a] [l]ine [o]f [l]yrics</td><td style="text-align:center;">T i a l o l</td></tr></tbody></table><p>Never do this:</p><ul><li>This [is a line of lyrics]</li></ul><p>Doing this will imply that the word <strong>This</strong> cannot ever be typed.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The CPM calculation will also ignore whatever is outside the brackets [ ].</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>You can know your CPM speed online, just search for typing tests.</p></div>',136),I=[x];function S(E,P,C,N,j,A){return s(),t("div",null,I)}var M=e(_,[["render",S]]);export{W as __pageData,M as default};
